import React, { Component } from "react";
import Jumbotron from "../../components/Jumbotron";
import API from "../../utils/API";
import "./Search.css";
import { Col, Row, Container } from "../../components/Grid";


class Search extends Component {

  state = {
    books: []
  };

  handleInputChange = event => {
    this.setState({ query: event.target.value.replace(" ", "+") });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.searchBooks(this.state.query)
      .then(res => this.setState ({ books: res.data.items }) )
      .catch(err => console.log(err));
    };

    handleSave = (event, index) => {
      event.preventDefault();
      let book = this.state.books[index];
      API.saveBook({
        title: book.volumeInfo.title,
        authors: book.volumeInfo.authors ? book.volumeInfo.authors.join(", ") : "",
        description:  book.volumeInfo.description,
        image:  book.volumeInfo.imageLinks ? book.volumeInfo.imageLinks.smallThumbnail : "",
        link: book.volumeInfo.infoLink
      })
        .then(res => alert("Book saved!"))
        .catch(err => console.log(err));
    };

  render () {
    return (
      <div>
        <Jumbotron>
          <h1>(React) Google Books Search</h1>
          <h2>Search for and Save Books of Interest</h2>
        </Jumbotron>

        {/* search form */}
        <div className="container">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Book Search</h5>
              <form className="form-inline my-2 my-lg-0">
                <input 
                  className="form-control mr-sm-2 mb-4 mt-2 col-12" 
                  id="search-input" 
                  type="search" 
                  placeholder="Title" 
                  aria-label="Search"
                  onChange={this.handleInputChange}
                 />
                <button 
                  className="btn btn-secondary my-sm-0 col-sm-12 col-md-4 col-lg-2" 
                  type="submit"
                  onClick={this.handleFormSubmit}
                >Search</button>
              </form>
            </div>
          </div>
        </div>

        {/* book card */}
        <Container>
          {this.state.books.length ? (
            <div>
              {this.state.books.map((book, index) => (   
                <div className="card my-3" key={book.id}>      
                  <div className="row no-gutters">
                    <div className="card-body book">
                      <h3 className="card-title search">{book.volumeInfo.title}</h3>
                      <p className="card-text">
                        <small className="text-muted search">
                          {book.volumeInfo.authors
                            ? book.volumeInfo.authors.join(", ")
                            : ""}
                        </small>
                      </p>
                      <div className="card-main row">
                        <div className="col-12 col-md-3">
                          <img 
                            src={book.volumeInfo.imageLinks 
                                  ? book.volumeInfo.imageLinks.smallThumbnail 
                                  : ""} 
                            className="card-img pb-3 search" 
                            alt="book thumbnail" />
                        </div>
                        <div className="col col-md-9">
                          <p className="card-text">{book.volumeInfo.description}</p>
                          <div className="buttons">
                            <a className="btn btn-info my-1 col-sm-12 col-md-4" 
                              href={book.volumeInfo.infoLink} 
                              role="button"
                              target="_blank" 
                              rel="noopener noreferrer">View</a>
                            <button 
                              className="btn btn-secondary my-1 col-sm-12 col-md-4" 
                              type="submit"
                              onClick={(e) => this.handleSave(e, index) }
                            >Save</button>
                          </div>
                        </div>
                      </div>
                    </div>                    
                  </div>   
                </div> 
              ))}
              </div> 
          ) : (
            <h2></h2>
          )}
        </Container>

      </div>
    );
  }
}

export default Search;
